import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as r,e as c}from"./app-058042d0.js";const d={},t=c('<h2 id="目录切换" tabindex="-1"><a class="header-anchor" href="#目录切换" aria-hidden="true">#</a> 目录切换</h2><ul><li><strong><code>cd usr</code>：</strong> 切换到该目录下 usr 目录</li><li><strong><code>cd ..（或cd../）</code>：</strong> 切换到上一层目录</li><li><strong><code>cd /</code>：</strong> 切换到系统根目录</li><li><strong><code>cd ~</code>：</strong> 切换到用户主目录</li><li><strong><code>cd -</code>：</strong> 切换到上一个操作所在目录</li></ul><h2 id="目录的操作命令" tabindex="-1"><a class="header-anchor" href="#目录的操作命令" aria-hidden="true">#</a> 目录的操作命令</h2><ul><li><p><strong><code>mkdir 目录名称</code>：</strong> 增加目录。</p></li><li><p><strong><code>ls/ll</code></strong>（ll 是 ls -l 的别名，ll 命令可以看到该目录下的所有目录和文件的详细信息）：查看目录信息。</p></li><li><p><strong><code>find 目录 参数</code>：</strong> 寻找目录（查）。</p></li><li><p><strong><code>mv 目录名称 新目录名称</code>：</strong> 修改目录的名称（改）。</p></li><li><p><strong><code>mv 目录名称 目录的新位置</code>：</strong> 移动目录的位置---剪切（改）。</p></li><li><p><strong><code>cp -r 目录名称 目录拷贝的目标位置</code>：</strong> 拷贝目录（改）</p></li><li><p><strong><code>rm [-rf] 目录</code> :</strong> 删除目录（删）。</p></li></ul><h2 id="文件操作" tabindex="-1"><a class="header-anchor" href="#文件操作" aria-hidden="true">#</a> 文件操作</h2><ul><li><p><strong><code>touch 文件名称</code>:</strong> 文件的创建（增）。</p></li><li><p><strong><code>cat/more/less/tail 文件名称</code></strong> ：文件的查看（查） 。</p></li><li><p><strong><code>vim 文件</code>：</strong> 修改文件的内容（改）。</p></li><li><p><strong><code>rm -rf 文件</code>：</strong> 删除文件（删）。</p></li></ul><h2 id="压缩文件" tabindex="-1"><a class="header-anchor" href="#压缩文件" aria-hidden="true">#</a> 压缩文件</h2><p>**<code>tar -zcvf</code> **打包压缩后的文件名 要与打包压缩的文件</p>',8),l=[t];function n(i,s){return e(),r("div",null,l)}const h=o(d,[["render",n],["__file","linux-common-commands.html.vue"]]);export{h as default};
